void loop() {
    webServer->Update();


    if(cameraReader->IsRunning() && cameraReader->HasNewFrame()){
        char * frame = nullptr;
        int length = cameraReader->GetFrame(frame);
        webServer->SendToAllClientsWithPage("image/jpeg", "/stream", frame, length);
        delete[] frame;
    }

    if(i == 100){
        webServer->SendToAllClientsWithPage("image/bmp", "/streamdemo", BMP_1, sizeof(BMP_1));
    }
    if(i == 200){
        webServer->SendToAllClientsWithPage("image/bmp", "/streamdemo", BMP_2, sizeof(BMP_2));
    }
    if(i == 300){
        webServer->SendToAllClientsWithPage("image/bmp", "/streamdemo", BMP_3, sizeof(BMP_3));
        i = 0;
    }
    GlobalVars::Uptime = millis();

    if(i==0){
        digitalWrite(ledPin, HIGH);
    }else{
        digitalWrite(ledPin, LOW);
    }

    delay(10);
    i++;
}